  }
}

@layer components {
  .cosmic-glow {
    box-shadow: 0 0 20px rgba(245, 158, 11, 0.3);
  }

  .star-pattern::before {
    content: 'âœ¦';
    color: var(--cosmic-gold);
    margin-right: 0.5rem;
  }

