import { GoogleGenAI } from "@google/genai";

const ai = new GoogleGenAI({ 
  apiKey: process.env.GEMINI_API_KEY || process.env.GOOGLE_AI_API_KEY || ""
});

export async function getAstrologicalResponse(userMessage: string): Promise<string> {
  try {
    const systemPrompt = `You are Nakshatra AI, an ancient, wise, and benevolent astrological guide. Your responses should be:
    - Accurate and insightful from an astrological perspective
    - Concise but meaningful (2-3 sentences max unless detailed explanation requested)
    - Empathetic and encouraging
    - Include relevant planetary influences, signs, and houses when appropriate
    - Use mystical language with cosmic emojis sparingly
    - If question is outside astrology, gently redirect to astrological topics
    - Never make definitive predictions about specific future events
    - Focus on guidance and cosmic influences rather than fortune telling

    Respond to this user's question about astrology:`;

    const response = await ai.models.generateContent({
      model: "gemini-2.5-flash",
      contents: `${systemPrompt}\n\nUser: ${userMessage}`,
    });

    return response.text || "ðŸŒŸ The cosmic energies are unclear at this moment. Please try rephrasing your question or ask about specific astrological influences.";
  } catch (error) {
    console.error("Gemini API error:", error);
    return "âœ¨ The stars are temporarily obscured. Please try again in a moment, and I'll be happy to share cosmic insights with you.";
  }
}

export async function generateKundaliSummary(birthDetails: {
  fullName: string;
  birthDate: Date;
  birthTime: string;
  birthPlace: string;
  gender: string;
}): Promise<string> {
  try {
    const prompt = `As an expert Vedic astrologer, provide a brief summary for a Kundali reading based on these birth details:

    Name: ${birthDetails.fullName}
    Birth Date: ${birthDetails.birthDate.toDateString()}
    Birth Time: ${birthDetails.birthTime}
    Birth Place: ${birthDetails.birthPlace}
    Gender: ${birthDetails.gender}

    Provide a 2-3 paragraph summary highlighting:
    - Key personality traits based on likely sun sign
    - General life themes and strengths
    - Areas for growth and awareness

    Keep it encouraging and insightful, avoiding specific predictions.`;

    const response = await ai.models.generateContent({
      model: "gemini-2.5-flash",
      contents: prompt,
    });

    return response.text || "Your cosmic blueprint reveals unique planetary influences that shape your spiritual journey.";
  } catch (error) {
    console.error("Gemini API error for Kundali summary:", error);
    return "The cosmic energies are aligning to reveal your celestial blueprint. A detailed analysis will be provided with your complete Kundali report.";
  }
}

client/src/components/AstroAIChat.tsx.content:
import { useState, useRef, useEffect } from "react";
import { Send, Bot, User, Clock, Brain, Lock } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

interface ChatMessage {
  id: string;
  message: string;
  isFromUser: boolean;
  timestamp: Date;
}

export default function AstroAIChat() {
  const [messages, setMessages] = useState<ChatMessage[]>([
    {
      id: "welcome",
      message: "ðŸŒŸ Namaste! I'm Nakshatra AI, your celestial guide. Ask me about your astrology, compatibility, or cosmic influences. How can I illuminate your path today?",
      isFromUser: false,
      timestamp: new Date(),
    }
  ]);
  const [inputMessage, setInputMessage] = useState("");
  const chatContainerRef = useRef<HTMLDivElement>(null);

  const chatMutation = useMutation({
    mutationFn: async (message: string) => {
      const response = await apiRequest("POST", "/api/chat", { message });
      return response.json();
    },
    onSuccess: (data) => {
      setMessages(prev => [...prev, {
        id: Date.now().toString(),
        message: data.response,
        isFromUser: false,
        timestamp: new Date(),
      }]);
    },
    onError: () => {
      setMessages(prev => [...prev, {
        id: Date.now().toString(),
        message: "âœ¨ The stars are temporarily obscured. Please try again in a moment, and I'll be happy to share cosmic insights with you.",
        isFromUser: false,
        timestamp: new Date(),
      }]);
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const message = inputMessage.trim();

    if (!message) return;

    // Add user message
    const userMessage: ChatMessage = {
      id: Date.now().toString(),
      message,
      isFromUser: true,
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage("");

    // Get AI response
    chatMutation.mutate(message);
  };

  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [messages]);

  return (
    <section id="astroai" className="py-20 px-6 bg-card/20">
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-serif font-bold mb-6">Your Cosmic Confidante</h2>
          <p className="text-xl text-muted-foreground max-w-3xl mx-auto">
            Connect with our AI-powered astrologer for instant insights and guidance. Ask about your signs, compatibility, or any astrological questions.
          </p>
        </div>

        <Card className="cosmic-border overflow-hidden">
          {/* Chat Header */}
          <CardHeader className="bg-gradient-to-r from-secondary/20 to-accent/20 border-b border-border">
            <div className="flex items-center space-x-4">
              <div className="w-12 h-12 bg-accent rounded-full flex items-center justify-center">
                <Bot className="text-accent-foreground text-xl" />
              </div>
              <div>
                <CardTitle className="text-lg">Nakshatra AI</CardTitle>
                <p className="text-muted-foreground text-sm">Your celestial guide â€¢ Online</p>
              </div>
            </div>
          </CardHeader>

          {/* Chat Messages */}
          <div 
            ref={chatContainerRef}
            className="h-96 overflow-y-auto p-6 space-y-4"
            data-testid="chat-container"
          >
            {messages.map((message) => (
              <div 
                key={message.id} 
                className={`flex items-start space-x-3 chat-message ${message.isFromUser ? 'flex-row-reverse space-x-reverse' : ''}`}
              >
                <div className="w-8 h-8 bg-accent rounded-full flex items-center justify-center flex-shrink-0">
                  {message.isFromUser ? (
                    <User className="text-accent-foreground text-sm" />
                  ) : (
                    <Bot className="text-accent-foreground text-sm" />
                  )}
                </div>
                <div className={`rounded-2xl px-4 py-3 max-w-md ${
                  message.isFromUser 
                    ? 'bg-accent/20' 
                    : 'bg-secondary/20'
                }`}>
                  <p className="text-sm">{message.message}</p>
                </div>
              </div>
            ))}

            {/* Typing Indicator */}
            {chatMutation.isPending && (
              <div className="flex items-start space-x-3 chat-message">
                <div className="w-8 h-8 bg-accent rounded-full flex items-center justify-center flex-shrink-0">
                  <Bot className="text-accent-foreground text-sm" />
                </div>
                <div className="bg-secondary/20 rounded-2xl px-4 py-3">
                  <div className="flex space-x-1">
                    <div className="w-2 h-2 bg-accent rounded-full animate-pulse"></div>
                    <div className="w-2 h-2 bg-accent rounded-full animate-pulse" style={{animationDelay: '0.2s'}}></div>
                    <div className="w-2 h-2 bg-accent rounded-full animate-pulse" style={{animationDelay: '0.4s'}}></div>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Chat Input */}
          <CardContent className="p-6 border-t border-border">
            <form className="flex space-x-4" onSubmit={handleSubmit}>
              <Input
                type="text"
                placeholder="Ask about your signs, planets, or cosmic guidance..."
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                className="flex-1"
                disabled={chatMutation.isPending}
                data-testid="input-chat-message"
              />
              <Button 
                type="submit" 
                disabled={chatMutation.isPending || !inputMessage.trim()}
                data-testid="button-send-message"
              >
                <Send className="w-4 h-4" />
              </Button>
            </form>
            <p className="text-xs text-muted-foreground mt-2 text-center">
              âœ¨ Powered by advanced AI with deep astrological knowledge
            </p>
          </CardContent>
        </Card>

        {/* Chat Features */}
        <div className="grid md:grid-cols-3 gap-6 mt-12">
          <Card className="text-center">
            <CardContent className="p-6">
              <Clock className="text-2xl text-accent mb-3 mx-auto" />
              <h4 className="font-semibold mb-2">24/7 Availability</h4>
              <p className="text-muted-foreground text-sm">Get cosmic guidance anytime, day or night</p>
            </CardContent>
          </Card>
          <Card className="text-center">
            <CardContent className="p-6">
              <Brain className="text-2xl text-secondary mb-3 mx-auto" />
              <h4 className="font-semibold mb-2">Ancient Wisdom</h4>
              <p className="text-muted-foreground text-sm">AI trained on thousands of years of astrological knowledge</p>
            </CardContent>
          </Card>
          <Card className="text-center">
            <CardContent className="p-6">
              <Lock className="text-2xl text-accent mb-3 mx-auto" />
              <h4 className="font-semibold mb-2">Private & Secure</h4>
              <p className="text-muted-foreground text-sm">Your cosmic conversations remain confidential</p>
            </CardContent>
          </Card>
        </div>
      </div>
    </section>
  );
}

client/src/components/CelestialStore.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { useCart } from "@/contexts/CartContext";
import { useToast } from "@/hooks/use-toast";
import { Product } from "@shared/schema";

const categories = [
  { id: "all", name: "All Products" },
  { id: "rings", name: "Rings" },
  { id: "stones", name: "Gemstones" },
  { id: "yantras", name: "Yantras" },
  { id: "books", name: "Books" },
  { id: "accessories", name: "Accessories" },
];

export default function CelestialStore() {
  const [selectedCategory, setSelectedCategory] = useState("all");
  const { addToCart } = useCart();
  const { toast } = useToast();

  const { data: products = [], isLoading, error } = useQuery<Product[]>({
    queryKey: ["/api/products", selectedCategory === "all" ? "" : `?category=${selectedCategory}`],
  });

  const handleAddToCart = (product: Product) => {
    addToCart({
      id: product.id,
      name: product.name,
      price: parseFloat(product.price),
      imageUrl: product.imageUrl || "",
      quantity: 1,
    });

    toast({
      title: "Added to Cart! âœ¨",
      description: `${product.name} has been added to your cosmic collection.`,
    });
  };

  if (error) {
    return (
      <section id="store" className="py-20 px-6">
        <div className="max-w-7xl mx-auto text-center">
          <h2 className="text-4xl md:text-5xl font-serif font-bold mb-6">Discover Celestial Treasures</h2>
          <p className="text-red-500">Unable to load products. Please try again later.</p>
        </div>
      </section>
    );
  }

  return (
    <section id="store" className="py-20 px-6">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-serif font-bold mb-6">Discover Celestial Treasures</h2>
          <p className="text-xl text-muted-foreground max-w-3xl mx-auto">
            Sacred artifacts and spiritual tools blessed by ancient wisdom and cosmic energies.
          </p>
        </div>

        {/* Product Categories */}
        <div className="flex flex-wrap justify-center gap-4 mb-12">
          {categories.map((category) => (
            <Button
              key={category.id}
              variant={selectedCategory === category.id ? "default" : "outline"}
              onClick={() => setSelectedCategory(category.id)}
              className="rounded-full transition-all hover:scale-105"
              data-testid={`category-${category.id}`}
            >
              {category.name}
            </Button>
          ))}
        </div>

        {/* Product Grid */}
        {isLoading ? (
          <div className="grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 mb-12">
            {[...Array(8)].map((_, i) => (
              <Card key={i} className="animate-pulse">
                <div className="w-full h-48 bg-muted rounded-t-lg"></div>
                <CardContent className="p-6">
                  <div className="h-4 bg-muted rounded mb-2"></div>
                  <div className="h-3 bg-muted rounded mb-4 w-3/4"></div>
                  <div className="flex items-center justify-between">
                    <div className="h-6 bg-muted rounded w-20"></div>
                    <div className="h-8 bg-muted rounded w-24"></div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : products.length === 0 ? (
          <div className="text-center py-12">
            <p className="text-muted-foreground text-lg">No products found in this category.</p>
            <p className="text-sm text-muted-foreground mt-2">
              Our cosmic inventory is being updated. Please check back soon.
            </p>
          </div>
        ) : (
          <div className="grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 mb-12">
            {products.map((product: Product) => (
              <Card key={product.id} className="product-card border border-border overflow-hidden hover:border-cosmic-gold/50 transition-all group">
                <Link href={`/product/${product.id}`}>
                  <img 
                    src={product.imageUrl || "https://images.unsplash.com/photo-1602173574767-37ac01994b2a?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300"} 
                    alt={product.name} 
                    className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-300 cursor-pointer"
                  />
                </Link>
                <CardContent className="p-6">
                  <Link href={`/product/${product.id}`}>
                    <h3 className="font-semibold text-lg mb-2 hover:text-cosmic-gold transition-colors cursor-pointer">{product.name}</h3>
                  </Link>
                  <p className="text-muted-foreground text-sm mb-4 line-clamp-2">{product.description}</p>
                  <div className="flex items-center justify-between">
                    <span className="text-2xl font-bold text-accent">â‚¹{product.price}</span>
                    <Button 
                      onClick={(e) => {
                        e.preventDefault();
                        e.stopPropagation();
                        handleAddToCart(product);
                      }}
                      className="font-medium transition-all cosmic-glow"
                      data-testid={`button-add-to-cart-${product.id}`}
                    >
                      Add to Cart
                    </Button>
                  </div>
                  {product.stock !== undefined && product.stock !== null && product.stock < 5 && (
                    <p className="text-orange-500 text-xs mt-2">Only {product.stock} left in stock!</p>
                  )}
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {/* Load Products Button for Demo */}
        {!isLoading && products.length === 0 && (
          <div className="text-center">
            <Button 
              onClick={() => {
                fetch("/api/seed-products", { method: "POST" })
                  .then(() => {
                    toast({
                      title: "Products Loaded! ðŸŒŸ",
                      description: "Sample celestial treasures have been added to the store.",
                    });
                    window.location.reload();
                  })
                  .catch(() => {
                    toast({
                      title: "Failed to Load Products",
                      description: "Please try again later.",
                      variant: "destructive",
                    });
                  });
              }}
              className="cosmic-glow"
              data-testid="button-seed-products"
            >
              Load Sample Products
            </Button>
          </div>
        )}
      </div>
    </section>
  );
}

