      case "orders":
        return <AdminOrderManagement />;
      case "kundali":
        return (
          <div className="text-center py-12">
            <h2 className="text-2xl font-bold mb-4">Kundali Types Management</h2>
            <p className="text-muted-foreground">Coming soon...</p>
